    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Log in!{% endblock %}

{% block body %}
<form method=\"post\">
    {% if error %}
        <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
    {% endif %}

    {% if app.user %}
        <div class=\"mb-3\">
            You are logged in as {{ app.user.username }}, <a href=\"{{ path('app_logout') }}\">Logout</a>

        </div>
    {% else %}

    <div class=\"font-sans\">
        <div class=\"relative min-h-screen flex flex-col sm:justify-center items-center bg-gray-100 \">
            <div class=\"relative sm:max-w-sm w-full\">
                <div class=\"card bg-gray-400 shadow-lg  w-full h-full rounded-3xl absolute  transform -rotate-6\"></div>
                <div class=\"card bg-red-700 shadow-lg  w-full h-full rounded-3xl absolute  transform rotate-6\"></div>
                <div class=\"relative w-full rounded-3xl  px-6 py-4 bg-gray-100 shadow-md\">



                    <h1 class=\"h3 mb-3 font-weight-normal\">Please sign in</h1>

                    <div class=\"mt-7\">
                        <label class=\"block text-sm font-medium text-black\" for=\"inputEmail\">Email</label>
                        <input type=\"email\" value=\"{{ last_username }}\" name=\"email\" id=\"inputEmail\" class=\"form-control\" required autofocus>
                    </div>

                    <div class=\"mt-7\">
                        <label  class=\"block text-sm font-medium text-black\" for=\"inputPassword\">Password</label>
                        <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\" required>
                    </div>

                    <div class=\"mt-7\">
                        <button type=\"submit\" class=\"bg-red-500 w-full py-3 rounded-xl text-white shadow-xl hover:shadow-inner focus:outline-none transition duration-500 ease-in-out  transform hover:-translate-x hover:scale-105\">
                            Login
                        </button>
                    </div>

                </div>
            </div>
        </div>
    </div>
    {% endif %}
</form>
{% endblock %}
", "security/login.html.twig", "/Users/maxencejoly/Desktop/W-PHP-502-MAR-2-2-Quiz-hugo.ribaud/my_quiz/templates/security/login.html.twig");
